css_files.content:
@charset "UTF-8";
@font-face {
  font-family: "Noto";
  src: local("Noto Sans"), local("NotoSans"), url("/static/_img/2025.01/fonts/NotoSans-Latin.ttf");
}
@font-face {
  font-family: "Noto";
  src: local("Noto Sans"), local("NotoSans"), url("/static/_img/2025.01/fonts/NotoSans-Latin-Italic.ttf");
  font-style: italic;
}
@font-face {
  font-family: "Source Code Pro";
  src: local("Source Code Pro"), url("/static/_img/2025.01/fonts/SourceCode.ttf");
}
@font-face {
  font-family: "Source Code Pro";
  src: local("Source Code Pro"), url("/static/_img/2025.01/fonts/SourceCode-Italic.ttf");
  font-style: italic;
}
@font-face {
  font-family: "s-arab";
  src: url("/static/_img/2025.01/fonts/NotoSans-Arabic.ttf");
}
.script-arab {
  font-family: "s-arab";
}

@font-face {
  font-family: "s-jpan";
  src: url("/static/_img/2025.01/fonts/NotoSans-Japanese.ttf");
}
.script-jpan {
  font-family: "s-jpan";
}

html, body, div, span, object, iframe, h1, h2, h3, h4, h5, h6,
p, blockquote, pre, a, abbr, address, cite, code, del, dfn, em,
img, ins, kbd, q, samp, small, strong, sub, sup, var, b, i, hr,
dl, dt, dd, ol, ul, li, fieldset, form, label, legend,
table, caption, tbody, tfoot, thead, tr, th, td,
article, aside, canvas, details, figure, figcaption, hgroup,
menu, footer, header, nav, section, summary, time, mark, audio, video {
  margin: 0;
  padding: 0;
  border: 0;
  font-weight: normal;
  font-style: normal;
  font-size: 100%;
  font-family: inherit;
}

ol, ul {
  list-style: none;
}

q:before, q:after, blockquote:before, blockquote:after {
  content: "";
}

table {
  border-collapse: collapse;
  border-spacing: 0;
}

article, aside, canvas, details, figcaption, figure, figure img,
footer, header, hgroup, nav, section, audio, video {
  display: block;
}

img, a img {
  border: 0;
}

::selection, ::-moz-selection {
  background: #ffff9e;
}

img::selection, img::-moz-selection {
  background: transparent;
}

body {
  -webkit-tap-highlight-color: #ffff9e;
}

html {
  -webkit-text-size-adjust: 100%;
}

sub,
sup {
  font-size: 75%;
  line-height: 0;
  position: relative;
  vertical-align: baseline;
}

sup {
  top: -0.5em;
}

sub {
  bottom: -0.25em;
}

button, input, select, textarea {
  font-size: 100%;
  margin: 0;
  vertical-align: baseline;
  *vertical-align: middle;
}

button,
input {
  line-height: normal;
}

button::-moz-focus-inner,
input::-moz-focus-inner {
  border: 0;
  padding: 0;
}

button,
input[type=button],
input[type=reset],
input[type=submit] {
  cursor: pointer;
  -webkit-appearance: button;
}

input[type=search] {
  -webkit-appearance: textfield;
  -webkit-box-sizing: content-box;
  -moz-box-sizing: content-box;
  box-sizing: content-box;
}

input[type=search]::-webkit-search-decoration {
  -webkit-appearance: none;
}

textarea {
  overflow: auto;
  vertical-align: top;
}

html {
  background: #ebeeef;
  min-height: 100%;
}

body {
  background: #fff;
  color: #555555;
  text-align: left;
  font-size: 16px;
  font-family: Noto, "Open Sans", "Helvetica Neue", Helvetica, sans-serif;
  font-synthesis: none;
  min-height: 100%;
}

/* Default layout  */
a:link, a:visited {
  color: #0069d6;
  text-decoration: none;
}
a:link .underline, a:visited .underline {
  text-decoration: underline;
}

p, td, th {
  margin: 1.2em 0;
}

b {
  font-weight: bold;
}

i, em {
  font-style: italic;
}

h1 {
  font-size: 28.8px;
  color: #000;
  font-weight: 500;
}
@media only screen and (max-width: 1000px) {
  h1 {
    font-size: 22.4px;
  }
}

h2 {
  font-size: 24px;
  margin: 1em 0 0.5em 0;
  color: #000;
  font-weight: 500;
}
@media only screen and (max-width: 1000px) {
  h2 {
    font-size: 19.2px;
  }
}

h3 {
  font-size: 19.2px;
  color: #000;
  font-weight: 500;
  margin-top: 1em;
}

h4 {
  color: #000;
  font-size: 16px;
  margin-top: 1em;
}

h5 {
  color: #000;
  margin-top: 1em;
  text-decoration: underline;
}

ul {
  display: block;
  list-style: square;
  margin: 1em 0;
}
@media only screen and (max-width: 1000px) {
  ul {
    margin: 0;
  }
}

ul li {
  list-style: square;
  margin-left: 2em;
  margin-top: 0.5em;
}
ul li li {
  margin-left: 2em;
  margin-top: 0;
}

ol {
  margin: 1em 0;
}
ol li {
  list-style: decimal;
  margin-left: 2em;
}

.lettered-list li {
  list-style: lower-roman;
}

b, strong {
  font-weight: bold;
}

strong {
  color: #000;
}

blockquote {
  margin-left: 40px;
}

ol {
  list-style: decimal;
}

dt {
  font-weight: bold;
}

dd {
  margin-left: 40px;
}

td {
  padding-right: 1em;
}

pre {
  font-family: "Source Code Pro", Consolas, monospace;
}

.caps {
  text-transform: uppercase;
  font-size: 93%;
}

hr.clear {
  display: block;
  clear: both;
  visibility: hidden;
  height: 0;
  border-width: 0;
  margin: 0;
  padding: 0;
}

.numbered_report {
  margin-left: 50px;
}
.numbered_report span.nr {
  float: left;
  margin-left: -50px;
  width: 50px;
}
.numbered_report .l2 {
  margin-left: 60px;
}
.numbered_report .l2 span.nr {
  width: 60px;
  margin-left: -60px;
}
.numbered_report .l3 {
  margin-left: 130px;
}
.numbered_report .l3 span.nr {
  width: 70px;
  margin-left: -70px;
}

.avoid-break {
  white-space: nowrap;
}

.domain {
  color: #2d474e;
  white-space: nowrap;
}
.email {
  text-transform: lowercase;
  font-family: "Source Code Pro", Consolas, monospace;
}

.domain-deux {
  background: #cadee5;
  border-radius: 4px;
  border: 1px solid #cadee5;
  margin: 2px;
  color: #2d474e;
  padding: 1px 3px;
  text-transform: lowercase;
  font-family: "Source Code Pro", Consolas, monospace;
}
.domain-deux a:link, .domain-deux a:visited {
  text-decoration: none;
  color: #2d474e;
}

.label {
  font-family: "Source Code Pro", Consolas, monospace;
  white-space: nowrap;
}
.label a:link, .label a:visited {
  text-decoration: none;
  color: #868cb6;
}

.panel_information {
  background-color: #cceeff;
  margin-top: 1em;
  border-top: 1px solid #b4c8dd;
  border-bottom: 1px solid #b4c8dd;
  padding: 10px;
}
.panel_information.icon {
  padding: 10px 10px 10px 60px;
  background-image: url(/static/_img/2022/icon_alert.svg);
  background-repeat: no-repeat;
  background-position: 10px 10px;
  padding: 10px 10px 10px 60px;
  min-height: 45px;
}

.panel_warning {
  background-color: #fff1bb;
  margin-top: 1em;
  border-top: 1px solid #f2de32;
  border-bottom: 1px solid #f2de32;
  padding: 10px;
}

.panel_error {
  background-color: #ffcfca;
  background-repeat: no-repeat;
  margin-top: 1em;
  border-top: 1px solid #f2896c;
  border-bottom: 1px solid #f2896c;
  padding: 10px;
}
.panel_error h2 {
  margin: 0;
  padding: 0;
  font-size: 14px;
}
.panel_error p, .panel_error ul {
  margin: 0;
}
.panel_error li {
  margin: 0 20px;
}

.excerpt {
  background: #fffff0;
  padding: 20px;
  border: 1px solid #f0f0d0;
}

.footnote-ref {
  border: 1px solid #666;
  border-radius: 3px;
  vertical-align: super;
  padding: 0px 2px;
  font-size: 10px;
  margin-left: 2px;
}
.footnote-ref:link, .footnote-ref:visited {
  text-decoration: none;
  color: #000;
}

.footnote-index {
  width: 30px;
  display: inline-block;
  text-indent: 0;
}
.footnote-index:after {
  content: ".";
}

.footnote {
  margin-left: 30px;
  text-indent: -30px;
  padding-bottom: 5px;
}

.email-address {
  font-weight: bold;
  white-space: nowrap;
}

#website-error {
  padding: 50px 10px;
}
#website-error h1 {
  color: #883745;
}

.force-rtl {
  direction: rtl;
}
.force-rtl:before {
  content: "‏";
}
.force-rtl:after {
  content: "‎";
}

.rfcref {
  background: #f0f0f8;
  border-radius: 4px;
  margin: 0px 2px 0px 0px;
  color: #2b3037;
  padding: 1px 4px;
  white-space: nowrap;
}
.rfcref a:link, .rfcref a:visited {
  text-decoration: none;
  color: #2b3037;
}

.rir-chart {
  position: relative;
  width: 65vw;
  height: 320px;
  padding-top: 5px;
  max-width: 840px;
}
@media only screen and (max-width: 1000px) {
  .rir-chart {
    width: 90vw;
  }
}
@media only screen and (max-width: 800px) {
  .rir-chart {
    height: 250px;
  }
}

.rir-footer {
  text-align: center;
  margin-top: 3em;
  color: #9999a0;
  font-size: 12px;
}

.rir-cur {
  width: 300px;
  height: 150px;
  float: left;
  font-size: 14px;
  padding-top: 10px;
}
@media only screen and (max-width: 1000px) {
  .rir-cur {
    float: none;
  }
}

.status-blue, .status-grey, .status-green {
  white-space: nowrap;
  border-radius: 11px;
  padding: 3px 9px;
  font-size: 10pt;
}

.status-blue {
  background: #bcdaed;
}

.status-green {
  background: #bcedc3;
}

.status-grey {
  background: #d1d1d1;
}

obf::after {
  content: attr(d);
}

obf::before {
  content: attr(u);
}

figure {
  margin: 1rem 0;
}

figcaption {
  margin-top: 0.5rem;
  font-size: 0.87rem;
  color: #555;
  line-height: 1.4;
}

/* @group Header Section */
header {
  width: 100%;
  background: #ebeeef;
  border-bottom: 1px solid #f0f0f0;
}
header.header_white {
  background: #ffffff;
}
header.header_warning {
  background: #fff1bb;
}

#header {
  max-width: 1100px;
  margin: 0 auto;
  padding: 25px 50px;
}
@media only screen and (max-width: 1000px) {
  #header {
    padding: 15px 25px;
  }
}
#header:before, #header:after {
  display: table;
  content: "";
  line-height: 0;
}
#header:after {
  clear: both;
}
#header #logo {
  float: left;
}
@media only screen and (max-width: 800px) {
  #header #logo img {
    width: 75%;
    height: 75%;
  }
}
#header .navigation {
  text-align: right;
  float: right;
}
@media only screen and (max-width: 800px) {
  #header .navigation {
    float: left;
    margin-top: 7px;
    clear: both;
  }
}
@media only screen and (max-width: 1000px) {
  #header .navigation ul {
    margin: 1em 0;
  }
}
#header .navigation li {
  list-style: none;
  display: inline;
  float: left;
  margin: 0;
}
#header .navigation li a {
  margin-left: 4px;
  padding: 4px 6px;
  text-decoration: none;
  font-size: 1em;
}
#header .navigation li a:link, #header .navigation li a:visited {
  color: rgb(46, 83, 105);
}
#header .navigation li a:hover {
  color: rgba(92, 166, 210, 0.8);
  transition: color 150ms ease-in;
}
@media only screen and (max-width: 800px) {
  #header .navigation li:first-child a {
    margin-left: 0;
    padding: 4px 0;
  }
}

#header_banner {
  margin: unset;
  padding: unset;
}
#header_banner a {
  text-decoration: none;
}

article.hemmed {
  width: 1100px;
  margin: 0 auto;
}
@media only screen and (max-width: 1199px) {
  article.hemmed {
    width: auto;
  }
}
article.sidenav {
  display: flex;
  flex-direction: row-reverse;
  padding: 25px 50px;
}
@media only screen and (max-width: 1000px) {
  article.sidenav {
    padding: 25px 25px;
  }
}

main {
  flex-grow: 1;
  flex-basis: 0;
  margin-top: 25px;
}
@media only screen and (max-width: 1000px) {
  main {
    margin-top: 0;
  }
}

#sidenav {
  flex-basis: 0;
  width: 250px;
  margin: 75px 0 0 0;
  font-size: 12px;
}
@media only screen and (max-width: 1000px) {
  #sidenav {
    display: none;
  }
}
#sidenav .navigation_box {
  border: 1px #f0f0f0 solid;
  border-radius: 5px;
  width: 225px;
  margin-right: 25px;
  background: #fcfcfc;
}
#sidenav a {
  text-decoration: none;
  color: inherit;
}
#sidenav ul {
  list-style: none;
}
#sidenav ul ul {
  margin: 0;
}
#sidenav ul ul li {
  padding: 2px 30px;
  font-size: 11px;
}
#sidenav li {
  list-style: none;
  margin: 0px 0 0 0px;
  padding: 2px 10px;
  font-size: 13px;
  font-color: #999;
}
#sidenav li.selected {
  font-weight: 700;
}
#sidenav li.selected:hover {
  -webkit-transition: none;
  background: inherit;
}
#sidenav li.selected > a {
  color: #000;
  font-weight: 700;
}
#sidenav li:hover {
  background: #f3f3f3;
  -webkit-transition: background 150ms ease-in;
}
#sidenav h2 {
  background: #f4f3f9;
  padding: 4px 10px;
  margin-top: 0;
  font-size: 14px;
  color: #333;
  font-weight: normal;
}

footer {
  width: 100%;
  background: #ebeeef;
  padding: 25px 0;
}
@media only screen and (max-width: 1000px) {
  footer {
    padding: 10px 0 10px;
  }
}

#footer {
  max-width: 1200px;
  margin: 0px auto;
  color: #9999a0;
  font-size: 12px;
}
#footer .navigation {
  margin: 10px 50px;
}
@media only screen and (max-width: 1000px) {
  #footer .navigation {
    margin: 10px 25px;
  }
}
#footer .navigation:link, #footer .navigation:visited {
  color: rgba(92, 166, 210, 0.8);
  -webkit-transition: color 150ms ease-in;
}
#footer .navigation td.section {
  padding: 5px 10px 5px 0;
  vertical-align: top;
}
#footer .navigation td.section a:link, #footer .navigation td.section a:visited {
  color: #9999a0;
  text-decoration: none;
  font-weight: bold;
}
#footer .navigation td.section a:hover {
  color: rgba(92, 166, 210, 0.8);
  -webkit-transition: color 150ms ease-in;
}
#footer .navigation td.subsection {
  padding: 5px 0px 5px 0;
}
#footer .navigation td.subsection a:link, #footer .navigation td.subsection a:visited {
  color: #9999a0;
  text-decoration: none;
}
#footer .navigation td.subsection a:hover {
  color: rgba(92, 166, 210, 0.8);
  -webkit-transition: color 150ms ease-in;
}
#footer .navigation ul {
  margin: 0;
}
#footer .navigation li {
  list-style: none;
  display: inline;
  float: left;
  margin: 0 5px 0 5px;
}
#footer #custodian {
  margin: 10px 50px;
  color: #9999a0;
}
@media only screen and (max-width: 1000px) {
  #footer #custodian {
    margin: 10px 25px;
  }
}
#footer #custodian:before, #footer #custodian:after {
  display: table;
  content: "";
  line-height: 0;
}
#footer #custodian:after {
  clear: both;
}
#footer #custodian a {
  white-space: nowrap;
}
#footer #custodian a:link, #footer #custodian a:visited {
  color: #9999a0;
  text-decoration: none;
  font-weight: bold;
}
#footer #custodian a:hover {
  color: rgba(92, 166, 210, 0.8);
  -webkit-transition: color 150ms ease-in;
}
@media only screen and (max-width: 1000px) {
  #footer #custodian a {
    white-space: inherit;
  }
}
#footer #custodian p {
  box-sizing: border-box;
}
#footer #custodian td:first, #footer #custodian p {
  padding: 0;
}
#footer #legalnotice {
  margin: 10px 50px;
  color: #9999a0;
}
@media only screen and (max-width: 1000px) {
  #footer #legalnotice {
    margin: 10px 25px;
  }
}
#footer #legalnotice:before, #footer #legalnotice:after {
  display: table;
  content: "";
  line-height: 0;
}
#footer #legalnotice:after {
  clear: both;
}
#footer #legalnotice a {
  white-space: nowrap;
}
#footer #legalnotice a:link, #footer #legalnotice a:visited {
  color: #9999a0;
  text-decoration: none;
  font-weight: bold;
}
#footer #legalnotice a:hover {
  color: rgba(92, 166, 210, 0.8);
  -webkit-transition: color 150ms ease-in;
}
@media only screen and (max-width: 1000px) {
  #footer #legalnotice a {
    white-space: inherit;
  }
}
#footer #legalnotice p {
  box-sizing: border-box;
}
#footer #legalnotice ul {
  margin: 0;
}
#footer #legalnotice li {
  list-style: none;
  display: inline;
  float: left;
  margin: 0 10px 0 0px;
}
#footer #legalnotice td:first, #footer #legalnotice p {
  padding: 0;
}

.section_ianatable {
  background-color: #f0f0f0;
  border: 1px solid #e0e0e0;
  padding: 10px;
  margin: 30px 0;
}

.iana-table {
  width: 100%;
  border-bottom: 1px solid #5eb9e6;
  border-collapse: separate;
}
.iana-table td {
  padding: 8px 8px 4px 4px;
  vertical-align: top;
  background: #fafafc;
}
.iana-table td:first-child {
  padding-left: 8px;
}
@media only screen and (max-width: 1000px) {
  .iana-table td {
    padding: 2px 10px;
    vertical-align: top;
    display: block;
    margin: 0;
  }
}
.iana-table thead tr {
  border-left: none;
  border-right: none;
}
.iana-table tbody tr {
  padding-top: 12px;
}
.iana-table th {
  padding: 4px 8px 4px 4px;
  color: #9d9d9d;
  font-size: 8pt;
  text-transform: uppercase;
  border-bottom: 1px solid #5eb9e6;
  vertical-align: bottom;
}
.iana-table th:first-child {
  padding-left: 8px;
}
@media only screen and (max-width: 1000px) {
  .iana-table th {
    display: none;
  }
}
@media only screen and (max-width: 1000px) {
  .iana-table tr {
    margin: 1em;
    border-bottom: 1px solid #5eb9e6;
  }
}
.iana-table tr:last {
  border-bottom: 2px solid #5eb9e6;
}
.iana-table tr:hover td {
  background-color: #f0f0f8;
}

.iana-table-navigation {
  background-color: #e0e0e0;
  padding: 5px;
  text-align: center;
}

form.framed {
  background: #f0f0f0;
}
form li {
  margin: 0;
}
form p {
  margin: 6px 30px 6px 10px;
}

#form {
  max-width: 1100px;
  padding: 25px 50px;
  margin: 0 auto;
}
@media only screen and (max-width: 1000px) {
  #form {
    padding: 25px 25px;
  }
}

.instructions {
  margin: 20px;
}

.form_sheet {
  background: #fcfcfc;
  border: 1px solid #ccc;
}

.form-field {
  background: #f0f0f0;
  padding: 10px 0px;
  margin: 0px 10px 10px;
  border-radius: 4px;
}

.required {
  font-size: 10px;
  color: #404040;
  margin-top: -5px;
  display: block;
}
@media only screen and (max-width: 1000px) {
  .required {
    display: inline;
  }
  .required:before {
    content: " (";
  }
  .required:after {
    content: ")";
  }
}

.no_navigation {
  margin-top: 60px;
  margin-bottom: 60px;
}

fieldset {
  padding-top: 10px;
}
fieldset h2 {
  display: block;
  margin: 10px 10px 5px 10px;
  font-size: 18px;
  font-weight: normal;
  color: rgb(63.75, 63.75, 63.75);
}
fieldset p {
  word-break: break-word;
}

.field:before, .field:after {
  display: table;
  content: "";
  line-height: 0;
}
.field:after {
  clear: both;
}

label {
  padding-top: 6px;
  font-size: 13px;
  font-weight: bold;
  line-height: 18px;
  float: left;
  width: 180px;
  text-align: right;
  color: rgb(63.75, 63.75, 63.75);
}
@media only screen and (max-width: 1000px) {
  label {
    float: none;
    padding-left: 10px;
  }
}

div.input {
  margin-left: 200px;
}
@media only screen and (max-width: 1000px) {
  div.input {
    margin-left: 10px;
  }
