.post .meta .with-icon--before .icon,
.post .meta .with-icon--before .avatar {
  height: 1.25cm !important;
  left: 0;
  position: absolute;
  top: 0;
  width: 1.25cm !important;
}

[dir=rtl] .post .meta .with-icon--before {
  padding-left: 0;
  padding-right: 1.5cm;
}
[dir=rtl] .post .meta .with-icon--before .icon,
[dir=rtl] .post .meta .with-icon--before .avatar {
  left: auto;
  right: 0;
}

.component--evangelists__list li {
  display: block;
}
.component--evangelists__list .sidebar {
  width: 3cm;
}

.component--evangelists__list a[href^=mailto]::after,
.component--evangelists__list a[href^=tel]::after,
.people-list a[href^=mailto]::after,
.people-list a[href^=tel]::after {
  display: none;
}

.people-list > li + li {
  margin-top: 30pt;
}

.people-list .l-cluster > * > * {
  display: block;
}

.people-list > li > div:not(.alumni) {
  padding-top: 75pt;
  position: relative;
}
.people-list > li > div:not(.alumni) h2, .people-list > li > div:not(.alumni) h3 {
  margin-top: 12pt;
}
.people-list > li > div:not(.alumni) > .sidebar {
  position: absolute;
  top: 0;
}

.people-list .sidebar {
  width: 75pt;
}

.event-list .card__image {
  width: 120pt;
}

.group-list a[href]::after {
  display: block;
}

.member-list .logo--member {
  display: none;
}

.post-list > li > div {
  min-height: 5cm;
  padding-right: 6cm;
  position: relative;
}
.post-list > li > div .l-frame {
  position: absolute;
  right: 0;
  top: 0;
  width: 6cm;
}
.post-list > li > div .l-frame::after {
  display: none !important;
}

[dir=rtl] .post-list > li > div {
  padding-right: 0;
  padding-left: 6cm;
}
[dir=rtl] .post-list > li > div .l-frame {
  left: 0;
  right: auto;
}

.tr-list__item * {
  font-size: 11pt;
}

.tr-list__item__header h3 {
  margin-bottom: 6pt;
  margin-top: 18pt;
}
.tr-list__item__header a {
  font-size: 12pt;
}

footer {
  margin-top: 36pt;
}

.skip-link,
.skip-to-comments,
.skip-to-comment-form,
#cookie-banner,
[role=status],
.banner,
#lang-nav,
.logo-link .visuallyhidden,
.heading-anchor,
[data-trigger],
[data-toggle],
#global-nav ul,
#breadcrumb,
.translations,
.toc,
.component--video,
.calendar-link,
.listing .lead,
.listing .filters,
.listing .browse,
.listing form,
.progress-list,
.autocomplete__wrapper svg,
.date-location img,
.card--group dl,
.card--group .l-cluster,
.people-list .icon,
.post-list .icon,
.post-list .tag,
.post .rss .icon,
.comments,
.pre-footer,
.pagination,
.crosslinks,
.global-footer__links {
  display: none !important;
}

